package ifElseStatement;

public class IfElseStatementClass1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 20;
		int b = 10;
		
		boolean cond=a>b;

		if (cond) {
			System.out.println("a is greater than b");
		} else {
			System.out.println("b is greater than a");
		}
	}

}
